package main

import (
	"bytes"
	"encoding/json"
	"io"
	"log"
	"net/http"
	"strings"
	"testing"
	"time"
)

func TestAPI(t *testing.T) {
	// all apis in 1 test since they are related
	sampleJpgBase64 := ""
	go startServer()

	time.Sleep(time.Duration(3) * time.Second)

	client := &http.Client{}

	data := PostReq{
		FileName:    "asd.jpg",
		Description: "123",
		Data:        sampleJpgBase64,
	}

	time.Sleep(time.Duration(1) * time.Second)

	body := []byte("somestring")
	response, statusCode, err := sendBytesParseJsonResponse(client, body, "POST", "http://127.0.0.1:8080/image")
	if err != nil {
		log.Fatal(err)
	}
	if statusCode < 400 {
		t.Error("Status code is wrong")
	}

	body, err = json.Marshal(data)
	response, statusCode, err = sendBytesParseJsonResponse(client, body, "POST", "http://127.0.0.1:8080/image")
	if err != nil {
		log.Fatal(err)
	}
	if statusCode >= 400 {
		t.Error("Status code is wrong")
	}
	if response["available"].(string) != "true" {
		t.Error("availability is wrong")
	}
	if response["description"].(string) != "123" {
		t.Error("description is wrong")
	}
}

func sendBytesParseJsonResponse(client *http.Client, body []byte, method, url string) (map[string]interface{}, int, error) {
	req, _ := http.NewRequest(strings.ToUpper(method), url, bytes.NewBuffer(body))
	req.Header.Add("Content-Type", "JSON")
	res, err := client.Do(req)
	if err != nil {
		return nil, -1, err
	}
	var jsonResponse map[string]interface{}
	b, err := io.ReadAll(res.Body)
	if err != nil {
		return nil, -1, err
	}
	err = json.Unmarshal(b, &jsonResponse)
	if err != nil {
		return nil, -1, err
	}
	return jsonResponse, res.StatusCode, nil
}
